async function cargarInspiracion() {
	const galeria = document.getElementById('galeria');
 galeria.textContent = "Cargando...";
 try {
 const res = await fetch('https://dog.ceo/api/breeds/image/random/6');
 const data = await res.json();
 galeria.textContent = "";
 data.message.forEach(url => {
 const img = document.createElement('img');
 img.src = url;
 img.alt = "Imagen de inspiración";
 img.loading = "lazy";
 img.width = 250;
 galeria.appendChild(img);
});
 } catch {
 galeria.textContent = "No se pudieron cargar las imágenes.";
 }
}
cargarInspiracion();